$--cyan: hsl(186, 34%, 60%); 
$--dark-brown: hsl(25, 47%, 15%); 
$--medium-brown: hsl(28, 10%, 53%); 
$--cream: hsl(27, 66%, 92%); 
$--very-pale-orange: hsl(33, 100%, 93%);
$--body-font-size: 18px; 
$--lightfont: 400; 
$--heavyfont: 700;

